import React, { useState, useRef, useCallback } from 'react'
import { motion } from 'framer-motion'
import { FileCode, Hammer, Image as ImageIcon, GitCompare, Upload, Download, RefreshCw, Wand2, ClipboardCopy } from 'lucide-react'

// =====================
// Helper (pure) functions
// =====================
const PLACEHOLDER_DESC = 'this item label was made in kuro item customizer this is just a place holder for missing descriptions'

function suggestFromName(name) {
  const key = (name || '').trim().toLowerCase().replace(/[^a-z0-9_\-]/g, '_')
  let label = (name || '').replace(/_/g, ' ').trim()
  if (label) label = label.charAt(0).toUpperCase() + label.slice(1)
  let type = 'item'
  let weight = 100
  if (/weapon|rifle|pistol/i.test(key)) { type = 'weapon'; weight = 2000 }
  else if (/burger|food|apple/i.test(key)) { type = 'food'; weight = 100 }
  return { key, label, weight, type, image: key ? `${key}.png` : '' }
}

function extractString(body, propNames, def = '') {
  const arr = Array.isArray(propNames) ? propNames : [propNames]
  for (const k of arr) {
    const m1 = body.match(new RegExp(`${k}\\s*=\\s*'([^']*)'`))
    if (m1) return m1[1]
    const m2 = body.match(new RegExp(`\\['${k}'\\]\\s*=\\s*'([^']*)'`))
    if (m2) return m2[1]
  }
  return def
}

function extractNumber(body, propName, def = 0) {
  const m = body.match(new RegExp(`${propName}\\s*=\\s*([0-9]+)`))
  return m ? Number(m[1]) : def
}

function extractBoolean(body, propName, def) {
  const mTrue = new RegExp(`${propName}\\s*=\\s*true`).test(body)
  const mFalse = new RegExp(`${propName}\\s*=\\s*false`).test(body)
  if (mTrue) return true
  if (mFalse) return false
  return def
}

function parseItemsFromLua(text) {
  const items = []
  if (!text) return items

  // Check if this is optimized format with add_item() calls
  if (text.includes('add_item(')) {
    return parseOptimizedFormat(text)
  }

  const collect = (key, body) => {
    const label = extractString(body, ['label', "['label']"], key)
    const weight = extractNumber(body, 'weight', 0)
    const type = extractString(body, 'type', 'item')
    const image = extractString(body, 'image', `${key}.png`)
    const unique = extractBoolean(body, 'unique', false)
    const useable = extractBoolean(body, 'useable', true)
    const shouldClose = extractBoolean(body, 'shouldClose', true)
    const descRaw = extractString(body, 'description', '')
    const description = descRaw || (label ? label : PLACEHOLDER_DESC)
    items.push({ key, label, weight, type, image, unique, useable, shouldClose, description })
  }

  // Enhanced parsing for QB items
  // Pattern A: ['key'] = { ... } with proper brace matching
  const lines = text.split('\n')
  let i = 0
  
  while (i < lines.length) {
    const line = lines[i].trim()
    
    // Check for item definition
    const itemMatch = line.match(/\['?([^'\]]+)'?\]\s*=\s*\{/) || line.match(/^([a-z0-9_\-]+)\s*=\s*\{/)
    
    if (itemMatch) {
      const key = itemMatch[1]
      let braceCount = 1
      let bodyLines = []
      i++
      
      // Collect the entire item body
      while (i < lines.length && braceCount > 0) {
        const currentLine = lines[i]
        bodyLines.push(currentLine)
        
        // Count braces to find the end
        const openBraces = (currentLine.match(/\{/g) || []).length
        const closeBraces = (currentLine.match(/\}/g) || []).length
        braceCount += openBraces - closeBraces
        
        i++
      }
      
      const body = bodyLines.join('\n')
      collect(key, body)
    } else {
      i++
    }
  }
  
  return items
}

function parseOptimizedFormat(text) {
  const items = []
  
  // Parse add_item('key', 'label', weight, 'type', 'image', unique, useable, 'description')
  const addItemPattern = /add_item\(\s*'([^']*)',\s*'([^']*)',\s*(\d+),\s*'([^']*)',\s*'([^']*)',\s*(true|false),\s*(true|false),\s*'([^']*)'\s*\)/g
  
  let match
  while ((match = addItemPattern.exec(text))) {
    const [, key, label, weight, type, image, unique, useable, description] = match
    
    items.push({
      key,
      label,
      weight: parseInt(weight),
      type,
      image,
      unique: unique === 'true',
      useable: useable === 'true',
      shouldClose: true,
      description: description || label || PLACEHOLDER_DESC
    })
  }
  
  return items
}

function toOptimized(items) {
  return items.map(({ key, label, weight, type, image, unique, useable, description }) =>
    `add_item('${key}', '${(label||'').replace(/'/g, "\\'")}', ${Number(weight)||0}, '${type}', '${image}', ${!!unique}, ${!!useable}, '${(description||'').replace(/'/g, "\\'")}')`
  ).join('\n')
}

function toPipe(items) {
  return items.map(({ key, label, weight, type, image, unique, useable, description }) =>
    `${key} | ${label} | ${Number(weight)||0} | ${type} | ${image} | ${!!unique} | ${!!useable} | ${description}`
  ).join('\n')
}

function toJson(items) {
  return items.map(({ key, label, weight, type, image, unique, useable, shouldClose, description }) => JSON.stringify({
    name: key, label, weight: Number(weight)||0, type, image,
    unique: !!unique, useable: !!useable, shouldClose: !!shouldClose, description
  })).join('\n')
}

function toOriginalFlat(items) {
  return items.map(({ key, label, weight, type, image, unique, useable, shouldClose, description }) =>
    `['${key}'] = { name = '${key}', label = '${label}', weight = ${Number(weight)||0}, type = '${type}', image = '${image}', unique = ${!!unique}, useable = ${!!useable}, shouldClose = ${!!shouldClose}, description = '${(description||'').replace(/'/g, "\\'")}' }`
  ).join('\n')
}

function toOX(items) {
  let output = 'return {\n'
  items.forEach(({ key, label, weight, type, image, unique, useable, description }) => {
    output += `\t['${key}'] = {\n`
    output += `\t\tlabel = '${label || key}',\n`
    
    // Add weight if different from default
    if (weight && weight !== 100) {
      output += `\t\tweight = ${Number(weight)||100},\n`
    }
    
    // Add stack property (opposite of unique)
    if (!unique) {
      output += `\t\tstack = true,\n`
    }
    
    // Add consume if useable
    if (useable) {
      output += `\t\tconsume = 0.3,\n`
    }
    
    // Client block
    output += `\t\tclient = {\n`
    output += `\t\t\timage = '${image || key + '.png'}',\n`
    
    if (useable) {
      output += `\t\t\tusetime = 2500,\n`
      output += `\t\t\tnotification = 'You used ${label || key}',\n`
    }
    
    // Add description as status if available
    if (description && description !== label && description !== PLACEHOLDER_DESC) {
      output += `\t\t\t-- description = '${description.replace(/'/g, "\\'")}',\n`
    }
    
    output += `\t\t},\n`
    
    // Server block only if useable
    if (useable) {
      output += `\t\tserver = {\n`
      output += `\t\t\texport = 'your_resource.${key}'\n`
      output += `\t\t},\n`
    }
    
    output += `\t},\n\n`
  })
  output += '}\n'
  return output
}

function parseOXItems(text) {
  const items = []
  if (!text) return items
  
  // Parse OX format: ['item_key'] = { ... }
  const oxPattern = /\['([^']+)'\]\s*=\s*\{([^}]+(?:\{[^}]*\}[^}]*)*)\}/g
  let match
  
  while ((match = oxPattern.exec(text))) {
    const key = match[1]
    const body = match[2]
    
    const label = extractString(body, 'label', key)
    const weight = extractNumber(body, 'weight', 100)
    const consume = extractNumber(body, 'consume', 0)
    const stack = extractBoolean(body, 'stack', true)
    const usetime = extractNumber(body, 'usetime', 0)
    const image = extractString(body, 'image', `${key}.png`)
    
    items.push({
      key,
      label,
      weight,
      type: 'item',
      image,
      unique: !stack,
      useable: consume > 0 || usetime > 0,
      shouldClose: true,
      description: label
    })
  }
  
  return items
}

function parsePipeFormat(text) {
  const items = []
  const lines = text.split('\n')
  
  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim()
    if (!line || line.includes('key|label|weight')) continue // Skip header
    
    const parts = line.split('|').map(part => part.trim())
    if (parts.length >= 7) {
      const [key, label, weight, type, image, unique, useable, ...descParts] = parts
      const description = descParts.join('|').trim()
      
      items.push({
        key,
        label: label || key,
        weight: parseInt(weight) || 100,
        type: type || 'item',
        image: image || `${key}.png`,
        unique: unique === 'true',
        useable: useable === 'true',
        shouldClose: true,
        description: description || label || PLACEHOLDER_DESC
      })
    }
  }
  
  return items
}

function parseJsonFormat(text) {
  const items = []
  
  try {
    let jsonData
    
    // Try parsing as array first
    if (text.trim().startsWith('[')) {
      jsonData = JSON.parse(text)
      if (Array.isArray(jsonData)) {
        jsonData.forEach(item => {
          items.push({
            key: item.name || item.key || 'unknown',
            label: item.label || item.name || item.key || 'Unknown',
            weight: item.weight || 100,
            type: item.type || 'item',
            image: item.image || `${item.key || item.name}.png`,
            unique: !!item.unique,
            useable: !!item.useable,
            shouldClose: item.shouldClose !== false,
            description: item.description || item.label || PLACEHOLDER_DESC
          })
        })
      }
    } else {
      // Try parsing as single object
      jsonData = JSON.parse(text)
      if (jsonData && typeof jsonData === 'object') {
        items.push({
          key: jsonData.name || jsonData.key || 'unknown',
          label: jsonData.label || jsonData.name || jsonData.key || 'Unknown',
          weight: jsonData.weight || 100,
          type: jsonData.type || 'item',
          image: jsonData.image || `${jsonData.key || jsonData.name}.png`,
          unique: !!jsonData.unique,
          useable: !!jsonData.useable,
          shouldClose: jsonData.shouldClose !== false,
          description: jsonData.description || jsonData.label || PLACEHOLDER_DESC
        })
      }
    }
  } catch (error) {
    console.error('JSON parsing error:', error)
  }
  
  return items
}

function parseAndConvert(inputText, format) {
  // Universal parser - detects input format automatically
  const items = parseAnyFormat(inputText)
  
  let out = ''
  switch (format) {
    case 'optimized': out = toOptimized(items); break
    case 'pipe': out = toPipe(items); break
    case 'json': out = toJson(items); break
    case 'ox': out = toOX(items); break
    case 'original': default: out = toOriginalFlat(items)
  }
  return { out, count: items.length }
}

function parseAnyFormat(text) {
  if (!text || !text.trim()) return []
  
  const trimmedText = text.trim()
  
  // Detect format and parse accordingly
  if (trimmedText.includes('add_item(')) {
    console.log('Detected: Optimized format')
    return parseOptimizedFormat(text)
  }
  
  if (trimmedText.includes('return {') && trimmedText.includes('client = {')) {
    console.log('Detected: OX Inventory format')
    return parseOXItems(text)
  }
  
  if (trimmedText.includes('|') && trimmedText.split('\n').some(line => line.split('|').length > 5)) {
    console.log('Detected: Pipe delimited format')
    return parsePipeFormat(text)
  }
  
  if ((trimmedText.startsWith('[') && trimmedText.includes('"name"')) || 
      (trimmedText.startsWith('{') && trimmedText.includes('"key"'))) {
    console.log('Detected: JSON format')
    return parseJsonFormat(text)
  }
  
  // Default to standard QB format
  console.log('Detected: Standard QB format')
  return parseItemsFromLua(text)
}

// Simple image helpers
function readFileAsDataURL(file) {
  return new Promise(function(resolvePromise, rejectPromise) {
    const fileReader = new FileReader()
    fileReader.addEventListener('load', function() {
      resolvePromise(fileReader.result)
    })
    fileReader.addEventListener('error', function() {
      rejectPromise(new Error('Failed to read file'))
    })
    fileReader.readAsDataURL(file)
  })
}

function dataURLToBlob(dataURL) {
  const [head, body] = String(dataURL).split(',')
  const mime = /data:(.*?);/.exec(head)?.[1] || 'image/png'
  const bstr = atob(body)
  const u8 = new Uint8Array(bstr.length)
  for (let i = 0; i < bstr.length; i++) u8[i] = bstr.charCodeAt(i)
  return new Blob([u8], { type: mime })
}

async function resizeImageData(dataUrl, targetWidth, targetHeight, maintainRatio = true) {
  return new Promise(function(resolvePromise, rejectPromise) {
    const imageElement = new Image()
    
    imageElement.addEventListener('load', function() {
      try {
        const canvasElement = document.createElement('canvas')
        const canvasContext = canvasElement.getContext('2d')
        
        if (maintainRatio) {
          // Smart resize: maintain aspect ratio, fit within bounds, center on transparent background
          const imageAspectRatio = imageElement.width / imageElement.height
          const targetAspectRatio = targetWidth / targetHeight
          
          let drawWidth, drawHeight, offsetX = 0, offsetY = 0
          
          if (imageAspectRatio > targetAspectRatio) {
            // Image is wider - fit to width
            drawWidth = targetWidth
            drawHeight = targetWidth / imageAspectRatio
            offsetY = (targetHeight - drawHeight) / 2
          } else {
            // Image is taller - fit to height
            drawHeight = targetHeight
            drawWidth = targetHeight * imageAspectRatio
            offsetX = (targetWidth - drawWidth) / 2
          }
          
          canvasElement.width = targetWidth
          canvasElement.height = targetHeight
          
          // Clear with transparent background
          canvasContext.clearRect(0, 0, targetWidth, targetHeight)
          
          // Draw image centered
          canvasContext.drawImage(imageElement, offsetX, offsetY, drawWidth, drawHeight)
        } else {
          // Stretch mode (old behavior)
          canvasElement.width = targetWidth
          canvasElement.height = targetHeight
          canvasContext.drawImage(imageElement, 0, 0, targetWidth, targetHeight)
        }
        
        resolvePromise(canvasElement.toDataURL('image/png'))
      } catch (errorObj) {
        rejectPromise(errorObj)
      }
    })
    
    imageElement.addEventListener('error', function() {
      rejectPromise(new Error('Failed to load image'))
    })
    
    imageElement.src = dataUrl
  })
}

// === Subcomponents (moved outside to prevent remounting) ===
const Header = () => (
  <motion.header 
    initial={{ opacity: 0, y: -8 }} 
    animate={{ opacity: 1, y: 0 }} 
    className='mb-8'
  >
    <div className='flex items-center gap-3 mb-2'>
      <div className='w-8 h-8 rounded-full bg-gradient-to-r from-kuro-green to-kuro-purple flex items-center justify-center'>
        <Wand2 size={16} className='text-white' />
      </div>
      <h1 className='text-2xl font-bold text-white'>Kuro Item Customizer</h1>
    </div>
    <p className='text-gray-400 text-sm'>
      Convert • Build • Resize • Diff — blazing-fast, zero-flash item management
    </p>
  </motion.header>
)

const Tabs = ({ tab, setTab }) => (
  <motion.nav initial={{ opacity: 0 }} animate={{ opacity: 1 }} className='mb-6'>
    <div className='flex flex-wrap gap-2 items-center'>
      {[
        { key: 'converter', label: 'Converter', icon: <FileCode size={16} /> },
        { key: 'builder', label: 'Item Builder', icon: <Hammer size={16} /> },
        { key: 'images', label: 'Image Resizer', icon: <ImageIcon size={16} /> },
        { key: 'diff', label: 'Diff Viewer', icon: <GitCompare size={16} /> },
      ].map(t => (
        <button
          key={t.key}
          onClick={() => setTab(t.key)}
          className={`group px-4 py-2 rounded-xl border text-sm flex items-center gap-2 transition ${
            tab === t.key 
              ? 'bg-gradient-to-r from-kuro-green/20 to-kuro-purple/20 border-kuro-green/40 text-white' 
              : 'bg-white/5 border-white/10 hover:border-white/20 text-gray-300'
          }`}
        >
          {t.icon}
          {t.label}
        </button>
      ))}
    </div>
  </motion.nav>
)

const Converter = ({ fileText, setFileText, outputText, processing, progress, itemsFound, format, setFormat, handleUpload, convert, downloadOutput }) => (
  <section>
    <div className='grid md:grid-cols-2 gap-6'>
      <motion.div initial={{ opacity: 0, x: -8 }} animate={{ opacity: 1, x: 0 }}>
        <div className='flex items-center justify-between mb-4'>
          <h2 className='text-lg font-semibold text-white'>Input</h2>
          <div className='flex items-center gap-2'>
            <span className='text-xs text-gray-400'>{itemsFound} items</span>
            <select 
              value={format} 
              onChange={e => setFormat(e.target.value)}
              className='px-3 py-1 rounded-lg bg-gray-800 border border-white/10 text-white text-sm focus:outline-none focus:ring-2 focus:ring-kuro-green/40'
            >
              <option value='optimized'>Optimized (recommended)</option>
              <option value='original'>Original QB Format</option>
              <option value='pipe'>Pipe Delimited</option>
              <option value='json'>JSON</option>
              <option value='ox'>OX Inventory</option>
            </select>
          </div>
        </div>
        <textarea
          value={fileText}
          onChange={e => setFileText(e.target.value)}
          placeholder='Paste your items.lua content here...'
          className='w-full h-96 p-4 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:outline-none focus:ring-2 focus:ring-kuro-green/40 text-white font-mono resize-none'
        />
        <div className='flex gap-2 mt-4'>
          <button 
            onClick={() => document.getElementById('fileInput')?.click()}
            className='px-4 py-2 rounded-xl bg-white/5 border border-white/10 text-gray-300 hover:bg-white/10 transition'
          >
            <Upload size={16} className='inline mr-2' />
            Upload items.lua
          </button>
          <input 
            id='fileInput' 
            type='file' 
            accept='.lua' 
            onChange={handleUpload} 
            className='hidden'
          />
        </div>
      </motion.div>
      
      <motion.div initial={{ opacity: 0, x: 8 }} animate={{ opacity: 1, x: 0 }}>
        <div className='flex items-center justify-between mb-4'>
          <h2 className='text-lg font-semibold text-white'>Output</h2>
          <button 
            onClick={downloadOutput}
            disabled={!outputText}
            className='px-4 py-2 rounded-xl bg-kuro-green hover:bg-kuro-green/80 text-white font-medium disabled:opacity-50 disabled:cursor-not-allowed transition'
          >
            <Download size={16} className='inline mr-2' />
            Download
          </button>
        </div>
        <div className='relative'>
          <textarea
            value={outputText}
            readOnly
            placeholder='Converted output will appear here...'
            className='w-full h-96 p-4 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 text-green-400 font-mono resize-none'
          />
          {processing && (
            <div className='absolute inset-0 bg-black/50 rounded-lg flex items-center justify-center'>
              <div className='text-center'>
                <RefreshCw size={24} className='animate-spin text-kuro-green mx-auto mb-2' />
                <div className='text-white text-sm'>Converting... {progress}%</div>
                <div className='w-32 h-2 bg-white/20 rounded-full mt-2 overflow-hidden'>
                  <div 
                    className='h-full bg-kuro-green transition-all duration-300'
                    style={{ width: `${progress}%` }}
                  />
                </div>
              </div>
            </div>
          )}
        </div>
        <button 
          onClick={convert}
          disabled={!fileText || processing}
          className='w-full mt-4 px-4 py-3 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple font-medium shadow-lg shadow-emerald-500/20 text-white disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 hover:shadow-emerald-500/30'
        >
          {processing ? 'Converting...' : 'Convert'}
        </button>
      </motion.div>
    </div>
  </section>
)

const ItemKeyInput = ({ value, onChange }) => (
  <input 
    value={value} 
    onChange={e => onChange(e.target.value)} 
    placeholder='custom_weapon' 
    className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:outline-none focus:ring-2 focus:ring-kuro-green/40 text-white'
  />
)

const Builder = ({ inputKey, builder, setBuilder, onBuilderNameChange, buildItem, outputText, setOutputText }) => (
  <section>
    <div className='grid md:grid-cols-2 gap-4'>
      <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
        <label className='text-sm text-gray-300'>Item Key</label>
        <ItemKeyInput value={inputKey} onChange={onBuilderNameChange} />
        <label className='text-sm text-gray-300 mt-3 block'>Label</label>
        <input 
          value={builder.label} 
          onChange={e => setBuilder(v => ({ ...v, label: e.target.value }))} 
          className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:outline-none focus:ring-2 focus:ring-kuro-green/40 text-white'
        />
        <div className='grid grid-cols-3 gap-2 mt-3'>
          <input 
            type='number' 
            value={builder.weight} 
            onChange={e => setBuilder(v => ({ ...v, weight: Number(e.target.value) }))} 
            className='p-2 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:ring-2 focus:ring-kuro-green/40 text-white'
          />
          <input 
            value={builder.type} 
            onChange={e => setBuilder(v => ({ ...v, type: e.target.value }))} 
            className='p-2 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:ring-2 focus:ring-kuro-green/40 text-white'
          />
          <input 
            value={builder.image} 
            onChange={e => setBuilder(v => ({ ...v, image: e.target.value }))} 
            className='p-2 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:ring-2 focus:ring-kuro-green/40 text-white'
          />
        </div>
        <div className='flex gap-2 mt-4'>
          <button 
            type="button"
            onClick={buildItem} 
            className='px-4 py-2 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple font-medium shadow-lg shadow-emerald-500/20 text-white'
          >
            Build Item
          </button>
          <button 
            type="button"
            onClick={() => { 
              setBuilder({ key: '', label: '', weight: 100, type: 'item', image: '' }); 
              setInputKey('');
              setOutputText(''); 
            }} 
            className='px-4 py-2 rounded-xl bg-white/5 border border-white/10 text-gray-300'
          >
            Clear
          </button>
        </div>
      </motion.div>
      <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
        <label className='text-sm text-gray-300'>Preview</label>
        <pre className='mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm overflow-auto font-mono border border-white/10 text-green-400'>
          {outputText || '-- built item will show here --'}
        </pre>
      </motion.div>
    </div>
  </section>
)

const ImagesTab = ({ images, setImages, maintainRatio, setMaintainRatio, processingImages, resizeProgress, resizedPreviews, onDropImages, handleResizeClick, processImages, downloadAllResized, downloadSingleResized }) => (
  <section>
    <motion.div 
      initial={{ opacity: 0, y: 8 }} 
      animate={{ opacity: 1, y: 0 }} 
      onDragOver={e => e.preventDefault()} 
      onDrop={onDropImages} 
      className='border-2 border-dashed border-white/15 p-6 rounded-xl text-center bg-black/20'
    >
      <p className='text-sm text-gray-300'>
        Drag & drop images here or{' '}
        <button 
          onClick={() => document.getElementById('imgIn')?.click()} 
          className='underline text-kuro-green'
        >
          browse
        </button>
      </p>
      <input 
        id='imgIn' 
        type='file' 
        multiple 
        accept='image/*' 
        onChange={onDropImages} 
        className='hidden'
      />
      <div className='mt-4 text-xs text-gray-400'>{images.length} image(s) ready</div>
      
      {/* Aspect Ratio Toggle */}
      <div className='mt-4 flex items-center justify-center gap-2'>
        <label className='flex items-center gap-2 text-sm text-gray-300'>
          <input
            type='checkbox'
            checked={maintainRatio}
            onChange={(e) => setMaintainRatio(e.target.checked)}
            className='rounded border-white/20 bg-white/10 text-kuro-green focus:ring-kuro-green'
          />
          Smart resize (maintain aspect ratio)
        </label>
      </div>
      
      <div className='flex gap-2 justify-center mt-4'>
        <button 
          onClick={handleResizeClick} 
          disabled={processingImages}
          className='px-4 py-2 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple font-medium shadow-lg shadow-emerald-500/20 text-white disabled:opacity-50'
        >
          {images.length > 0 ? 'Select & Resize Images' : 'Select Images'}
        </button>
        <button 
          onClick={processImages} 
          disabled={processingImages || !images.length}
          className='px-4 py-2 rounded-xl bg-blue-600 hover:bg-blue-700 font-medium text-white disabled:opacity-50'
        >
          {processingImages ? 'Processing...' : 'Process Images'}
        </button>
        <button 
          onClick={() => { setImages([]); setResizedPreviews([]); setResizeProgress(0) }} 
          className='px-4 py-2 rounded-xl bg-white/5 border border-white/10 text-gray-300'
        >
          Clear All
        </button>
      </div>
      
      {/* Progress Bar */}
      {(processingImages || resizeProgress > 0) && (
        <div className='mt-4'>
          <div className='bg-white/10 h-2 rounded-full overflow-hidden'>
            <motion.div 
              initial={{ width: 0 }} 
              animate={{ width: `${resizeProgress}%` }} 
              className='h-full bg-kuro-green'
            />
          </div>
          <div className='text-xs text-gray-400 mt-1'>{resizeProgress}% complete</div>
        </div>
      )}
      
      {/* Download Section */}
      {resizedPreviews.length > 0 && (
        <div className='mt-6 border-t border-white/10 pt-6'>
          <div className='flex justify-between items-center mb-4'>
            <h3 className='text-lg font-semibold text-white'>Processed Images ({resizedPreviews.length})</h3>
            <button 
              onClick={downloadAllResized}
              className='px-4 py-2 rounded-xl bg-green-600 hover:bg-green-700 font-medium text-white'
            >
              Download All
            </button>
          </div>
          
          <div className='grid grid-cols-1 md:grid-cols-2 gap-4 max-h-96 overflow-y-auto'>
            {resizedPreviews.map((preview, index) => (
              <div key={index} className='bg-white/5 rounded-lg p-4'>
                <div className='flex gap-4'>
                  <div className='flex-1'>
                    <h4 className='text-sm font-medium text-white mb-2'>{preview.name}</h4>
                    <div className='flex gap-2'>
                      <div className='text-center'>
                        <div className='text-xs text-gray-400 mb-1'>Original</div>
                        <img src={preview.original} alt='Original' className='w-16 h-16 object-cover rounded border border-white/20' />
                      </div>
                      <div className='text-center'>
                        <div className='text-xs text-gray-400 mb-1'>Resized (100x100)</div>
                        <img src={preview.resized} alt='Resized' className='w-16 h-16 object-cover rounded border border-white/20' />
                      </div>
                    </div>
                  </div>
                  <button 
                    onClick={() => downloadSingleResized(preview)}
                    className='px-3 py-2 rounded-lg bg-kuro-green hover:bg-kuro-green/80 text-white text-sm'
                  >
                    <Download size={16} />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </motion.div>
    <div className='mt-4 grid grid-cols-2 md:grid-cols-6 gap-2'>
      {images.map((f, i) => (
        <motion.div 
          key={i} 
          initial={{ opacity: 0, y: 6 }} 
          animate={{ opacity: 1, y: 0 }} 
          className='bg-[#0b0f1a] p-2 rounded-lg text-xs text-gray-300 border border-white/10 truncate'
        >
          {f.name}
        </motion.div>
      ))}
    </div>
  </section>
)

const DiffTab = ({ diffBefore, diffAfter }) => (
  <section>
    <div className='grid md:grid-cols-2 gap-6'>
      <motion.div initial={{ opacity: 0, x: -8 }} animate={{ opacity: 1, x: 0 }}>
        <h2 className='text-lg font-semibold text-white mb-4'>Before</h2>
        <pre className='w-full h-96 p-4 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 text-red-400 font-mono overflow-auto'>
          {diffBefore || '-- original content will show here --'}
        </pre>
      </motion.div>
      <motion.div initial={{ opacity: 0, x: 8 }} animate={{ opacity: 1, x: 0 }}>
        <h2 className='text-lg font-semibold text-white mb-4'>After</h2>
        <pre className='w-full h-96 p-4 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 text-green-400 font-mono overflow-auto'>
          {diffAfter || '-- converted content will show here --'}
        </pre>
      </motion.div>
    </div>
  </section>
)

const WelcomeScreen = ({ onGetStarted }) => (
  <motion.div 
    initial={{ opacity: 0, scale: 0.95 }} 
    animate={{ opacity: 1, scale: 1 }} 
    className='text-center py-16'
  >
    <div className='mb-8'>
      <div className='w-20 h-20 rounded-full bg-gradient-to-r from-kuro-green to-kuro-purple flex items-center justify-center mx-auto mb-6'>
        <Wand2 size={32} className='text-white' />
      </div>
      <h1 className='text-4xl font-bold text-white mb-4'>Welcome to Kuro Item Customizer</h1>
      <p className='text-gray-400 text-lg max-w-2xl mx-auto'>
        The ultimate tool for QBCore item management. Convert formats, build items, resize images, and compare changes with ease.
      </p>
    </div>
    
    <div className='grid md:grid-cols-2 lg:grid-cols-4 gap-6 mb-12 max-w-4xl mx-auto'>
      <motion.div 
        initial={{ opacity: 0, y: 20 }} 
        animate={{ opacity: 1, y: 0 }} 
        transition={{ delay: 0.1 }}
        className='bg-white/5 rounded-xl p-6 border border-white/10'
      >
        <FileCode size={24} className='text-kuro-green mx-auto mb-3' />
        <h3 className='text-white font-semibold mb-2'>Universal Converter</h3>
        <p className='text-gray-400 text-sm'>Convert between QB, OX, JSON, and pipe formats</p>
      </motion.div>
      
      <motion.div 
        initial={{ opacity: 0, y: 20 }} 
        animate={{ opacity: 1, y: 0 }} 
        transition={{ delay: 0.2 }}
        className='bg-white/5 rounded-xl p-6 border border-white/10'
      >
        <Hammer size={24} className='text-kuro-green mx-auto mb-3' />
        <h3 className='text-white font-semibold mb-2'>Item Builder</h3>
        <p className='text-gray-400 text-sm'>Create new items with smart suggestions</p>
      </motion.div>
      
      <motion.div 
        initial={{ opacity: 0, y: 20 }} 
        animate={{ opacity: 1, y: 0 }} 
        transition={{ delay: 0.3 }}
        className='bg-white/5 rounded-xl p-6 border border-white/10'
      >
        <ImageIcon size={24} className='text-kuro-green mx-auto mb-3' />
        <h3 className='text-white font-semibold mb-2'>Image Resizer</h3>
        <p className='text-gray-400 text-sm'>Batch resize with smart aspect ratio</p>
      </motion.div>
      
      <motion.div 
        initial={{ opacity: 0, y: 20 }} 
        animate={{ opacity: 1, y: 0 }} 
        transition={{ delay: 0.4 }}
        className='bg-white/5 rounded-xl p-6 border border-white/10'
      >
        <GitCompare size={24} className='text-kuro-green mx-auto mb-3' />
        <h3 className='text-white font-semibold mb-2'>Diff Viewer</h3>
        <p className='text-gray-400 text-sm'>Compare before and after changes</p>
      </motion.div>
    </div>
    
    <motion.button 
      initial={{ opacity: 0, y: 20 }} 
      animate={{ opacity: 1, y: 0 }} 
      transition={{ delay: 0.5 }}
      onClick={onGetStarted}
      className='px-8 py-4 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple font-medium shadow-lg shadow-emerald-500/20 text-white hover:shadow-emerald-500/30 transition-all duration-200'
    >
      Get Started
    </motion.button>
  </motion.div>
)

export default function ItemCustomizer() {
  const [showWelcome, setShowWelcome] = useState(true)
  const [tab, setTab] = useState('converter')
  const [fileText, setFileText] = useState('')
  const [outputText, setOutputText] = useState('')
  const [processing, setProcessing] = useState(false)
  const [progress, setProgress] = useState(0)
  const [itemsFound, setItemsFound] = useState(0)
  const [format, setFormat] = useState('optimized')
  const fileInputRef = useRef(null)
  const [builder, setBuilder] = useState({ key: '', label: '', weight: 100, type: 'item', image: '' })
  const [inputKey, setInputKey] = useState('')
  const builderRef = useRef({ key: '', label: '', weight: 100, type: 'item', image: '' })
  const [images, setImages] = useState([])
  const [resizeProgress, setResizeProgress] = useState(0)
  const [processingImages, setProcessingImages] = useState(false)
  const [resizedPreviews, setResizedPreviews] = useState([])
  const [maintainRatio, setMaintainRatio] = useState(true)
  const [diffBefore, setDiffBefore] = useState('')
  const [diffAfter, setDiffAfter] = useState('')

  function readFileAsText(file) {
    return new Promise((res, rej) => {
      const r = new FileReader()
      r.onload = () => res(r.result)
      r.onerror = rej
      r.readAsText(file)
    })
  }

  async function handleUpload(e) {
    const f = e.target.files?.[0]
    if (!f) return
    const txt = await readFileAsText(f)
    setFileText(txt)
    setDiffBefore(txt)
    const parsed = parseItemsFromLua(txt)
    setItemsFound(parsed.length)
  }

  async function convert() {
    if (!fileText) return
    setProcessing(true)
    setProgress(0)
    setOutputText('')
    
    try {
      // Simulate progress
      for (let i = 0; i < 5; i++) {
        await new Promise(r => setTimeout(r, 100))
        setProgress(Math.round(((i + 1) / 5) * 100))
      }
      
      const { out, count } = parseAndConvert(fileText, format)
      setItemsFound(count)
      setOutputText(out || '-- No items detected. Ensure your file uses a supported format. --')
      setDiffAfter(out)
    } catch (error) {
      console.error('Conversion error:', error)
      setOutputText(`-- Error during conversion: ${error.message} --`)
    } finally {
      setProcessing(false)
      setProgress(100)
    }
  }

  function downloadOutput() {
    if (!outputText) return
    const blob = new Blob([outputText], { type: 'text/plain' })
    const url = URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = format === 'json' ? 'converted_items.json' : format === 'pipe' ? 'converted_items.txt' : 'converted_items.lua'
    a.click()
    URL.revokeObjectURL(url)
  }

  const onBuilderNameChange = useCallback((val) => {
    setInputKey(val)
    builderRef.current = { ...builderRef.current, key: val }
  }, [])

  const buildItem = useCallback(() => {
    const currentBuilder = builderRef.current
    const desc = currentBuilder.label ? currentBuilder.label : PLACEHOLDER_DESC
    const originalBlock = `['${currentBuilder.key}'] = { name = '${currentBuilder.key}', label = '${currentBuilder.label}', weight = ${Number(currentBuilder.weight)||0}, type = '${currentBuilder.type}', image = '${currentBuilder.image}', unique = false, useable = true, shouldClose = true, description = '${(desc||'').replace(/'/g, "\\'")}' }`
    const { out } = parseAndConvert(originalBlock, format)
    setOutputText(out)
    setDiffAfter(out)
  }, [format])

  function onDropImages(e) {
    e.preventDefault()
    const files = Array.from(e.dataTransfer?.files || e.target.files || [])
    const imgs = files.filter(f => f.type.startsWith('image/'))
    setImages(imgs)
  }

  function handleResizeClick() {
    if (images.length > 0) {
      // If images are already selected, resize them
      resizeAll()
    } else {
      // If no images, open file browser
      document.getElementById('imgIn')?.click()
    }
  }

  async function processImages() {
    if (!images.length) {
      alert('Please select images first!')
      return
    }
    
    setProcessingImages(true)
    setResizeProgress(0)
    const total = images.length
    const previews = []
    
    try {
      for (let i = 0; i < images.length; i++) {
        const file = images[i]
        console.log(`Processing ${file.name}...`)
        
        // Validate file is actually an image
        if (!file.type.startsWith('image/')) {
          console.warn(`Skipping non-image file: ${file.name}`)
          continue
        }
        
        const dataUrl = await readFileAsDataURL(file)
        const resized = await resizeImageData(dataUrl, 100, 100, maintainRatio)
        
        previews.push({
          name: file.name,
          original: dataUrl,
          resized: resized,
          blob: dataURLToBlob(resized)
        })
        
        setResizeProgress(Math.round(((i + 1) / total) * 100))
        
        // Small delay for smooth progress
        await new Promise(resolve => setTimeout(resolve, 50))
      }
      
      setResizedPreviews(previews)
      if (previews.length > 0) {
        alert(`Successfully processed ${previews.length} images! Review and download below.`)
      } else {
        alert('No valid images were processed. Please ensure you selected image files.')
      }
    } catch (error) {
      console.error('Processing error:', error)
      alert('Error processing images: ' + error.message)
    } finally {
      setProcessingImages(false)
    }
  }

  function downloadAllResized() {
    if (!resizedPreviews.length) {
      alert('No processed images to download!')
      return
    }
    
    resizedPreviews.forEach((preview, index) => {
      setTimeout(() => {
        const url = URL.createObjectURL(preview.blob)
        const a = document.createElement('a')
        a.href = url
        a.download = `resized_${preview.name.replace(/\.[^/.]+$/, '.png')}`
        document.body.appendChild(a)
        a.click()
        document.body.removeChild(a)
        URL.revokeObjectURL(url)
      }, index * 200) // Stagger downloads
    })
  }

  function downloadSingleResized(preview) {
    const url = URL.createObjectURL(preview.blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `resized_${preview.name.replace(/\.[^/.]+$/, '.png')}`
    document.body.appendChild(a)
    a.click()
    document.body.removeChild(a)
    URL.revokeObjectURL(url)
  }

  async function resizeAll() {
    // Legacy function for backward compatibility
    await processImages()
  }

  // ==== Subcomponents ====
  const Header = () => (
    <motion.header 
      initial={{ opacity: 0, y: -8 }} 
      animate={{ opacity: 1, y: 0 }} 
      transition={{ duration: .3 }} 
      className='mb-6'
    >
      <div className='flex items-center justify-between'>
        <div className='flex items-center gap-3'>
          <div className='h-9 w-9 rounded-xl bg-gradient-to-br from-kuro-green to-kuro-purple shadow-lg shadow-emerald-500/10' />
          <div>
            <h1 className='text-xl md:text-2xl font-semibold tracking-tight text-white'>Kuro Item Customizer</h1>
            <p className='text-xs text-gray-400'>Convert • Build • Resize • Diff — blazing-fast, zero deps</p>
          </div>
        </div>
        <div className='flex gap-2 items-center text-sm text-gray-300'>
          <span className='px-2 py-1 rounded-full bg-white/5 border border-white/10'>{itemsFound} items</span>
          <span className='px-2 py-1 rounded-full bg-white/5 border border-white/10'>Format: {format}</span>
        </div>
      </div>
    </motion.header>
  )

  const Tabs = () => (
    <motion.nav initial={{ opacity: 0 }} animate={{ opacity: 1 }} className='mb-6'>
      <div className='flex flex-wrap gap-2 items-center'>
        {[
          { key: 'converter', label: 'Converter', icon: <FileCode size={16} /> },
          { key: 'builder', label: 'Item Builder', icon: <Hammer size={16} /> },
          { key: 'images', label: 'Image Resizer', icon: <ImageIcon size={16} /> },
          { key: 'diff', label: 'Diff Viewer', icon: <GitCompare size={16} /> },
        ].map(t => (
          <button
            key={t.key}
            onClick={() => setTab(t.key)}
            className={`group px-4 py-2 rounded-xl border text-sm flex items-center gap-2 transition ${
              tab === t.key 
                ? 'bg-gradient-to-r from-kuro-green/20 to-kuro-purple/20 border-kuro-green/40 text-white' 
                : 'bg-white/5 border-white/10 hover:border-white/20 text-gray-300'
            }`}
          >
            <span className={`${tab === t.key ? 'text-kuro-green' : 'text-gray-300 group-hover:text-white'}`}>
              {t.icon}
            </span>
            {t.label}
          </button>
        ))}
        <div className='ml-auto flex gap-2 items-center'>
          <select 
            value={format} 
            onChange={e => setFormat(e.target.value)} 
            className='bg-gray-800 border border-white/10 px-3 py-2 rounded-xl text-sm text-white focus:outline-none focus:ring-2 focus:ring-kuro-green/40'
            style={{
              backgroundImage: `url("data:image/svg+xml;charset=UTF-8,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='white' stroke-width='2' stroke-linecap='round' stroke-linejoin='round'%3e%3cpolyline points='6,9 12,15 18,9'%3e%3c/polyline%3e%3c/svg%3e")`,
              backgroundRepeat: 'no-repeat',
              backgroundPosition: 'right 8px center',
              backgroundSize: '16px',
              paddingRight: '32px',
              appearance: 'none'
            }}
          >
            <option value='optimized' style={{ backgroundColor: '#1f2937', color: 'white' }}>Optimized (recommended)</option>
            <option value='original' style={{ backgroundColor: '#1f2937', color: 'white' }}>Original Block</option>
            <option value='pipe' style={{ backgroundColor: '#1f2937', color: 'white' }}>Compact Pipe</option>
            <option value='json' style={{ backgroundColor: '#1f2937', color: 'white' }}>JSON</option>
            <option value='ox' style={{ backgroundColor: '#1f2937', color: 'white' }}>OX Inventory</option>
          </select>
          <button 
            onClick={() => fileInputRef.current?.click()} 
            className='px-3 py-2 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple text-sm font-medium shadow-lg shadow-emerald-500/20 flex items-center gap-2 text-white'
          >
            <Upload size={16} /> Upload items.lua
          </button>
          <input ref={fileInputRef} type='file' accept='.lua,.txt' onChange={handleUpload} className='hidden' />
        </div>
      </div>
    </motion.nav>
  )

  const Converter = () => (
    <section>
      <div className='grid md:grid-cols-2 gap-4'>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <div className='flex items-center justify-between'>
            <label className='text-sm text-gray-300'>Input (items.lua)</label>
            <kbd className='text-[10px] px-2 py-1 rounded bg-black/40 border border-white/10 text-gray-300'>Ctrl + V</kbd>
          </div>
          <textarea 
            value={fileText} 
            onChange={e => setFileText(e.target.value)} 
            rows={14} 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm font-mono border border-white/10 focus:outline-none focus:ring-2 focus:ring-kuro-green/40 text-white'
          />
        </motion.div>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <div className='flex items-center justify-between'>
            <label className='text-sm text-gray-300'>Output</label>
            <div className='flex gap-2'>
              <button 
                onClick={() => navigator.clipboard.writeText(outputText)} 
                disabled={!outputText} 
                className='text-xs px-2 py-1 rounded border border-white/10 bg-white/5 hover:border-white/20 flex items-center gap-1 text-gray-300'
              >
                <ClipboardCopy size={14} /> Copy
              </button>
              <button 
                onClick={downloadOutput} 
                disabled={!outputText} 
                className='text-xs px-2 py-1 rounded border border-white/10 bg-white/5 hover:border-white/20 flex items-center gap-1 text-gray-300'
              >
                <Download size={14} /> Download
              </button>
            </div>
          </div>
          <textarea 
            value={outputText} 
            readOnly 
            rows={14} 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm font-mono border border-white/10 text-green-400'
          />
        </motion.div>
      </div>
      <div className='flex items-center gap-3 mt-4'>
        <button 
          onClick={convert} 
          disabled={processing || !fileText} 
          className={`px-4 py-2 rounded-xl font-medium shadow-lg flex items-center gap-2 text-white transition-all ${
            processing || !fileText 
              ? 'bg-gray-600 cursor-not-allowed' 
              : 'bg-gradient-to-r from-kuro-green to-kuro-purple shadow-emerald-500/20 hover:shadow-emerald-500/30'
          }`}
        >
          <Wand2 size={16} /> {processing ? 'Converting...' : 'Convert'}
        </button>
        <div className='flex-1 h-2 rounded-full bg-white/10 overflow-hidden'>
          <div 
            className='h-full bg-kuro-green transition-all duration-300'
            style={{ width: `${progress}%` }}
          />
        </div>
        <div className='text-sm text-gray-300 w-16 text-right'>{progress}%</div>
      </div>
      <div className='text-xs text-gray-400 mt-3 flex items-start gap-2'>
        ⚠️ <span>Parser reads unique/useable/shouldClose from source. Missing description falls back to label, then placeholder.</span>
      </div>
    </section>
  )

  const Builder = () => (
    <section>
      <div className='grid md:grid-cols-2 gap-4'>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <label className='text-sm text-gray-300'>Item Key</label>
          <input 
            value={inputKey} 
            onChange={e => onBuilderNameChange(e.target.value)} 
            placeholder='custom_weapon' 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:outline-none focus:ring-2 focus:ring-kuro-green/40 text-white'
          />
          <label className='text-sm text-gray-300 mt-3 block'>Label</label>
          <input 
            value={builder.label} 
            onChange={e => setBuilder(v => ({ ...v, label: e.target.value }))} 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:outline-none focus:ring-2 focus:ring-kuro-green/40 text-white'
          />
          <div className='grid grid-cols-3 gap-2 mt-3'>
            <input 
              type='number' 
              value={builder.weight} 
              onChange={e => setBuilder(v => ({ ...v, weight: Number(e.target.value) }))} 
              className='p-2 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:ring-2 focus:ring-kuro-green/40 text-white'
            />
            <input 
              value={builder.type} 
              onChange={e => setBuilder(v => ({ ...v, type: e.target.value }))} 
              className='p-2 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:ring-2 focus:ring-kuro-green/40 text-white'
            />
            <input 
              value={builder.image} 
              onChange={e => setBuilder(v => ({ ...v, image: e.target.value }))} 
              className='p-2 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:ring-2 focus:ring-kuro-green/40 text-white'
            />
          </div>
          <div className='flex gap-2 mt-4'>
            <button 
              type="button"
              onClick={buildItem} 
              className='px-4 py-2 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple font-medium shadow-lg shadow-emerald-500/20 text-white'
            >
              Build Item
            </button>
            <button 
              type="button"
              onClick={() => { 
                setBuilder({ key: '', label: '', weight: 100, type: 'item', image: '' }); 
                setInputKey('');
                setOutputText(''); 
              }} 
              className='px-4 py-2 rounded-xl bg-white/5 border border-white/10 text-gray-300'
            >
              Clear
            </button>
          </div>
        </motion.div>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <label className='text-sm text-gray-300'>Preview</label>
          <pre className='mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm overflow-auto font-mono border border-white/10 text-green-400'>
            {outputText || '-- built item will show here --'}
          </pre>
        </motion.div>
      </div>
    </section>
  )

  const ImagesTab = () => (
    <section>
      <motion.div 
        initial={{ opacity: 0, y: 8 }} 
        animate={{ opacity: 1, y: 0 }} 
        onDragOver={e => e.preventDefault()} 
        onDrop={onDropImages} 
        className='border-2 border-dashed border-white/15 p-6 rounded-xl text-center bg-black/20'
      >
        <p className='text-sm text-gray-300'>
          Drag & drop images here or{' '}
          <button 
            onClick={() => document.getElementById('imgIn')?.click()} 
            className='underline text-kuro-green'
          >
            browse
          </button>
        </p>
        <input 
          id='imgIn' 
          type='file' 
          multiple 
          accept='image/*' 
          onChange={onDropImages} 
          className='hidden'
        />
        <div className='mt-4 text-xs text-gray-400'>{images.length} image(s) ready</div>
        
        {/* Aspect Ratio Toggle */}
        <div className='mt-4 flex items-center justify-center gap-2'>
          <label className='flex items-center gap-2 text-sm text-gray-300'>
            <input
              type='checkbox'
              checked={maintainRatio}
              onChange={(e) => setMaintainRatio(e.target.checked)}
              className='rounded border-white/20 bg-white/10 text-kuro-green focus:ring-kuro-green'
            />
            Smart resize (maintain aspect ratio)
          </label>
        </div>
        
        <div className='flex gap-2 justify-center mt-4'>
          <button 
            onClick={handleResizeClick} 
            disabled={processingImages}
            className='px-4 py-2 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple font-medium shadow-lg shadow-emerald-500/20 text-white disabled:opacity-50'
          >
            {images.length > 0 ? 'Select & Resize Images' : 'Select Images'}
          </button>
          <button 
            onClick={processImages} 
            disabled={processingImages || !images.length}
            className='px-4 py-2 rounded-xl bg-blue-600 hover:bg-blue-700 font-medium text-white disabled:opacity-50'
          >
            {processingImages ? 'Processing...' : 'Process Images'}
          </button>
          <button 
            onClick={() => { setImages([]); setResizedPreviews([]); setResizeProgress(0) }} 
            className='px-4 py-2 rounded-xl bg-white/5 border border-white/10 text-gray-300'
          >
            Clear All
          </button>
        </div>
        {/* Progress Bar */}
        {(processingImages || resizeProgress > 0) && (
          <div className='mt-4'>
            <div className='bg-white/10 h-2 rounded-full overflow-hidden'>
              <motion.div 
                initial={{ width: 0 }} 
                animate={{ width: `${resizeProgress}%` }} 
                className='h-full bg-kuro-green'
              />
            </div>
            <div className='text-xs text-gray-400 mt-1'>{resizeProgress}% complete</div>
          </div>
        )}
        
        {/* Download Section */}
        {resizedPreviews.length > 0 && (
          <div className='mt-6 border-t border-white/10 pt-6'>
            <div className='flex justify-between items-center mb-4'>
              <h3 className='text-lg font-semibold text-white'>Processed Images ({resizedPreviews.length})</h3>
              <button 
                onClick={downloadAllResized}
                className='px-4 py-2 rounded-xl bg-green-600 hover:bg-green-700 font-medium text-white'
              >
                Download All
              </button>
            </div>
            
            <div className='grid grid-cols-1 md:grid-cols-2 gap-4 max-h-96 overflow-y-auto'>
              {resizedPreviews.map((preview, index) => (
                <div key={index} className='bg-white/5 rounded-lg p-4'>
                  <div className='flex gap-4'>
                    <div className='flex-1'>
                      <h4 className='text-sm font-medium text-white mb-2'>{preview.name}</h4>
                      <div className='flex gap-2'>
                        <div className='text-center'>
                          <div className='text-xs text-gray-400 mb-1'>Original</div>
                          <img src={preview.original} alt='Original' className='w-16 h-16 object-cover rounded border border-white/20' />
                        </div>
                        <div className='text-center'>
                          <div className='text-xs text-gray-400 mb-1'>Resized (100x100)</div>
                          <img src={preview.resized} alt='Resized' className='w-16 h-16 object-cover rounded border border-white/20' />
                        </div>
                      </div>
                    </div>
                    <button 
                      onClick={() => downloadSingleResized(preview)}
                      className='px-3 py-2 rounded-lg bg-kuro-green hover:bg-kuro-green/80 text-white text-sm'
                    >
                      <Download size={16} />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </motion.div>
      <div className='mt-4 grid grid-cols-2 md:grid-cols-6 gap-2'>
        {images.map((f, i) => (
          <motion.div 
            key={i} 
            initial={{ opacity: 0, y: 6 }} 
            animate={{ opacity: 1, y: 0 }} 
            className='bg-[#0b0f1a] p-2 rounded-lg text-xs text-gray-300 border border-white/10 truncate'
          >
            {f.name}
          </motion.div>
        ))}
      </div>
    </section>
  )

  const DiffTab = () => (
    <section>
      <div className='grid md:grid-cols-2 gap-4'>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <label className='text-sm text-gray-300'>Before</label>
          <textarea 
            value={diffBefore} 
            onChange={e => setDiffBefore(e.target.value)} 
            rows={14} 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm font-mono border border-white/10 text-white'
          />
        </motion.div>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <div className='flex items-center justify-between'>
            <label className='text-sm text-gray-300'>After</label>
            <div className='flex gap-2'>
              <button 
                onClick={() => setDiffAfter(outputText)} 
                className='text-xs px-2 py-1 rounded border border-white/10 bg-white/5 hover:border-white/20 flex items-center gap-1 text-gray-300'
              >
                <RefreshCw size={14} /> From Output
              </button>
              <button 
                onClick={() => navigator.clipboard.writeText(diffAfter)} 
                className='text-xs px-2 py-1 rounded border border-white/10 bg-white/5 hover:border-white/20 flex items-center gap-1 text-gray-300'
              >
                <ClipboardCopy size={14} /> Copy
              </button>
            </div>
          </div>
          <textarea 
            value={diffAfter} 
            onChange={e => setDiffAfter(e.target.value)} 
            rows={14} 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm font-mono border border-white/10 text-green-400'
          />
        </motion.div>
      </div>
      <div className='mt-4 text-xs text-gray-400'>
        Review changes before replacing your server file. Always keep a backup.
      </div>
    </section>
  )

  // Welcome Screen Component
  const WelcomeScreen = () => (
    <div className='min-h-screen relative bg-[radial-gradient(ellipse_at_top,_var(--tw-gradient-stops))] from-[#0b1220] via-[#0b0f1a] to-black text-gray-100 flex items-center justify-center'>
      <div className='pointer-events-none absolute inset-0 opacity-20 [mask-image:radial-gradient(transparent,black)]'>
        <div className='absolute inset-0 bg-[linear-gradient(90deg,rgba(255,255,255,.06)_1px,transparent_1px),linear-gradient(rgba(255,255,255,.06)_1px,transparent_1px)] bg-[size:32px_32px]' />
      </div>
      <motion.div 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className='relative text-center max-w-2xl px-8'
      >
        <motion.div 
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.6 }}
          className='w-32 h-32 mx-auto mb-8 rounded-3xl bg-gradient-to-br from-kuro-green to-kuro-purple shadow-2xl shadow-emerald-500/20 flex items-center justify-center'
        >
          <span className='text-5xl'>🛠️</span>
        </motion.div>
        
        <motion.h1 
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4, duration: 0.6 }}
          className='text-5xl md:text-6xl font-bold mb-6 bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent'
        >
          Kuro Item Customizer
        </motion.h1>
        
        <motion.p 
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.6, duration: 0.6 }}
          className='text-xl text-gray-300 mb-12 leading-relaxed'
        >
          The ultimate tool for FiveM item management.<br />
          Convert • Build • Resize • Compare — blazing-fast, zero dependencies.
        </motion.p>
        
        <motion.div 
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.8, duration: 0.6 }}
          className='space-y-4'
        >
          <button
            onClick={() => setShowWelcome(false)}
            className='px-8 py-4 bg-gradient-to-r from-kuro-green to-kuro-purple text-white font-semibold rounded-2xl shadow-2xl shadow-emerald-500/25 hover:shadow-emerald-500/40 transition-all duration-300 transform hover:scale-105'
          >
            Get Started
          </button>
          
          <div className='flex items-center justify-center gap-8 text-sm text-gray-400 mt-8'>
            <div className='flex items-center gap-2'>
              <span className='w-2 h-2 bg-kuro-green rounded-full'></span>
              <span>QB ↔ OX Conversion</span>
            </div>
            <div className='flex items-center gap-2'>
              <span className='w-2 h-2 bg-kuro-purple rounded-full'></span>
              <span>5 Export Formats</span>
            </div>
            <div className='flex items-center gap-2'>
              <span className='w-2 h-2 bg-blue-400 rounded-full'></span>
              <span>Image Resizing</span>
            </div>
          </div>
        </motion.div>
      </motion.div>
    </div>
  )

  if (showWelcome) {
    return <WelcomeScreen />
  }

  return (
    <div className='min-h-screen relative bg-[radial-gradient(ellipse_at_top,_var(--tw-gradient-stops))] from-[#0b1220] via-[#0b0f1a] to-black text-gray-100'>
      {/* subtle grid overlay */}
      <div className='pointer-events-none absolute inset-0 opacity-20 [mask-image:radial-gradient(transparent,black)]'>
        <div className='absolute inset-0 bg-[linear-gradient(90deg,rgba(255,255,255,.06)_1px,transparent_1px),linear-gradient(rgba(255,255,255,.06)_1px,transparent_1px)] bg-[size:32px_32px]' />
      </div>
      <div className='relative p-6 max-w-6xl mx-auto'>
        <Header />
        <Tabs tab={tab} setTab={setTab} />
        <main className='rounded-2xl border border-white/10 bg-white/5 backdrop-blur-xl p-4 md:p-5 shadow-xl shadow-black/30'>
          {tab === 'converter' && (
            <Converter 
              fileText={fileText}
              setFileText={setFileText}
              outputText={outputText}
              processing={processing}
              progress={progress}
              itemsFound={itemsFound}
              format={format}
              setFormat={setFormat}
              handleUpload={handleUpload}
              convert={convert}
              downloadOutput={downloadOutput}
            />
          )}
          {tab === 'builder' && (
            <Builder 
              inputKey={inputKey}
              builder={builder}
              setBuilder={setBuilder}
              onBuilderNameChange={onBuilderNameChange}
              buildItem={buildItem}
              outputText={outputText}
              setOutputText={setOutputText}
            />
          )}
          {tab === 'images' && (
            <ImagesTab 
              images={images}
              setImages={setImages}
              maintainRatio={maintainRatio}
              setMaintainRatio={setMaintainRatio}
              processingImages={processingImages}
              resizeProgress={resizeProgress}
              resizedPreviews={resizedPreviews}
              onDropImages={onDropImages}
              handleResizeClick={handleResizeClick}
              processImages={processImages}
              downloadAllResized={downloadAllResized}
              downloadSingleResized={downloadSingleResized}
            />
          )}
          {tab === 'diff' && (
            <DiffTab 
              diffBefore={diffBefore}
              diffAfter={diffAfter}
            />
          )}
        </main>
        <footer className='mt-6 text-xs text-gray-400 flex flex-wrap items-center gap-2'>
          <span>Made for QBCore item conversion.</span>
          <span className='opacity-50'>•</span>
          <span>No flashing animations (epilepsy‑safe).</span>
          <span className='opacity-50'>•</span>
          <span>Fav accent: <span className='text-kuro-green'>#55fa9a</span></span>
        </footer>
      </div>
    </div>
  )
}
