import React, { useState, useRef } from 'react'
import { motion } from 'framer-motion'
import { FileCode, Hammer, Image, GitCompare, Upload, Download, RefreshCw, Wand2, ClipboardCopy } from 'lucide-react'

// =====================
// Helper (pure) functions
// =====================
const PLACEHOLDER_DESC = 'this item label was made in kuro item customizer this is just a place holder for missing descriptions'

function suggestFromName(name) {
  const key = (name || '').trim().toLowerCase().replace(/[^a-z0-9_\-]/g, '_')
  let label = (name || '').replace(/_/g, ' ').trim()
  if (label) label = label.charAt(0).toUpperCase() + label.slice(1)
  let type = 'item'
  let weight = 100
  if (/weapon|rifle|pistol/i.test(key)) { type = 'weapon'; weight = 2000 }
  else if (/burger|food|apple/i.test(key)) { type = 'food'; weight = 100 }
  return { key, label, weight, type, image: key ? `${key}.png` : '' }
}

function extractString(body, propNames, def = '') {
  const arr = Array.isArray(propNames) ? propNames : [propNames]
  for (const k of arr) {
    const m1 = body.match(new RegExp(`${k}\\s*=\\s*'([^']*)'`))
    if (m1) return m1[1]
    const m2 = body.match(new RegExp(`\\['${k}'\\]\\s*=\\s*'([^']*)'`))
    if (m2) return m2[1]
  }
  return def
}

function extractNumber(body, propName, def = 0) {
  const m = body.match(new RegExp(`${propName}\\s*=\\s*([0-9]+)`))
  return m ? Number(m[1]) : def
}

function extractBoolean(body, propName, def) {
  const mTrue = new RegExp(`${propName}\\s*=\\s*true`).test(body)
  const mFalse = new RegExp(`${propName}\\s*=\\s*false`).test(body)
  if (mTrue) return true
  if (mFalse) return false
  return def
}

function parseItemsFromLua(text) {
  const items = []
  if (!text) return items

  const collect = (key, body) => {
    const label = extractString(body, ['label', "['label']"], key)
    const weight = extractNumber(body, 'weight', 0)
    const type = extractString(body, 'type', 'item')
    const image = extractString(body, 'image', `${key}.png`)
    const unique = extractBoolean(body, 'unique', false)
    const useable = extractBoolean(body, 'useable', true)
    const shouldClose = extractBoolean(body, 'shouldClose', true)
    const descRaw = extractString(body, 'description', '')
    const description = descRaw || (label ? label : PLACEHOLDER_DESC)
    items.push({ key, label, weight, type, image, unique, useable, shouldClose, description })
  }

  // Pattern A: ['key'] = { ... }
  const rxA = /\['?([a-z0-9_\-]+)'?\]\s*=\s*\{([\s\S]*?)\n\s*\}/gi
  let m
  while ((m = rxA.exec(text))) { collect(m[1], m[2]) }

  // Pattern B: key = { ... } (no brackets)
  const rxB = /\n\s*([a-z0-9_\-]+)\s*=\s*\{([\s\S]*?)\n\s*\}/gi
  while ((m = rxB.exec(text))) {
    const key = m[1]
    if (!items.find(it => it.key === key)) collect(key, m[2])
  }
  return items
}

function toOptimized(items) {
  return items.map(({ key, label, weight, type, image, unique, useable, description }) =>
    `add_item('${key}', '${(label||'').replace(/'/g, "\\'")}', ${Number(weight)||0}, '${type}', '${image}', ${!!unique}, ${!!useable}, '${(description||'').replace(/'/g, "\\'")}')`
  ).join('\n')
}

function toPipe(items) {
  return items.map(({ key, label, weight, type, image, unique, useable, description }) =>
    `${key} | ${label} | ${Number(weight)||0} | ${type} | ${image} | ${!!unique} | ${!!useable} | ${description}`
  ).join('\n')
}

function toJson(items) {
  return items.map(({ key, label, weight, type, image, unique, useable, shouldClose, description }) => JSON.stringify({
    name: key, label, weight: Number(weight)||0, type, image,
    unique: !!unique, useable: !!useable, shouldClose: !!shouldClose, description
  })).join('\n')
}

function toOriginalFlat(items) {
  return items.map(({ key, label, weight, type, image, unique, useable, shouldClose, description }) =>
    `['${key}'] = { name = '${key}', label = '${label}', weight = ${Number(weight)||0}, type = '${type}', image = '${image}', unique = ${!!unique}, useable = ${!!useable}, shouldClose = ${!!shouldClose}, description = '${(description||'').replace(/'/g, "\\'")}' }`
  ).join('\n')
}

function toOX(items) {
  let output = 'return {\n'
  items.forEach(({ key, label, weight, type, image, unique, useable, description }) => {
    output += `\t['${key}'] = {\n`
    output += `\t\tlabel = '${label}',\n`
    if (weight && weight !== 100) {
      output += `\t\tweight = ${Number(weight)||0},\n`
    }
    if (useable) {
      output += `\t\tconsume = 0.3,\n`
    }
    if (!unique) {
      output += `\t\tstack = true,\n`
    }
    output += `\t\tclient = {\n`
    output += `\t\t\timage = '${image}',\n`
    if (useable) {
      output += `\t\t\tusetime = 2500,\n`
      output += `\t\t\tnotification = 'You used ${label}',\n`
    }
    output += `\t\t},\n`
    if (useable) {
      output += `\t\tserver = {\n`
      output += `\t\t\texport = 'your_resource.${key}'\n`
      output += `\t\t},\n`
    }
    output += `\t},\n\n`
  })
  output += '}\n'
  return output
}

function parseOXItems(text) {
  const items = []
  if (!text) return items
  
  // Parse OX format: ['item_key'] = { ... }
  const oxPattern = /\['([^']+)'\]\s*=\s*\{([^}]+(?:\{[^}]*\}[^}]*)*)\}/g
  let match
  
  while ((match = oxPattern.exec(text))) {
    const key = match[1]
    const body = match[2]
    
    const label = extractString(body, 'label', key)
    const weight = extractNumber(body, 'weight', 100)
    const consume = extractNumber(body, 'consume', 0)
    const stack = extractBoolean(body, 'stack', true)
    const usetime = extractNumber(body, 'usetime', 0)
    const image = extractString(body, 'image', `${key}.png`)
    
    items.push({
      key,
      label,
      weight,
      type: 'item',
      image,
      unique: !stack,
      useable: consume > 0 || usetime > 0,
      shouldClose: true,
      description: label
    })
  }
  
  return items
}

function parseAndConvert(luaText, format) {
  // Check if input is OX format
  const isOXFormat = luaText.includes('return {') && luaText.includes('client = {')
  const items = isOXFormat ? parseOXItems(luaText) : parseItemsFromLua(luaText)
  
  let out = ''
  switch (format) {
    case 'optimized': out = toOptimized(items); break
    case 'pipe': out = toPipe(items); break
    case 'json': out = toJson(items); break
    case 'ox': out = toOX(items); break
    case 'original': default: out = toOriginalFlat(items)
  }
  return { out, count: items.length }
}

// Simple image helpers
function readFileAsDataURL(file) {
  return new Promise((res, rej) => {
    const r = new FileReader()
    r.onload = () => res(r.result)
    r.onerror = rej
    r.readAsDataURL(file)
  })
}

function dataURLToBlob(dataURL) {
  const [head, body] = String(dataURL).split(',')
  const mime = /data:(.*?);/.exec(head)?.[1] || 'image/png'
  const bstr = atob(body)
  const u8 = new Uint8Array(bstr.length)
  for (let i = 0; i < bstr.length; i++) u8[i] = bstr.charCodeAt(i)
  return new Blob([u8], { type: mime })
}

function resizeDataUrl(dataUrl, width, height) {
  return new Promise((res) => {
    const img = new Image()
    img.onload = () => {
      const c = document.createElement('canvas')
      c.width = width; c.height = height
      const ctx = c.getContext('2d')
      // center-crop
      const ar = img.width / img.height
      let sx = 0, sy = 0, sw = img.width, sh = img.height
      if (ar > 1) { sw = img.height; sx = (img.width - img.height) / 2 }
      else { sh = img.width; sy = (img.height - img.width) / 2 }
      ctx.drawImage(img, sx, sy, sw, sh, 0, 0, width, height)
      res(c.toDataURL('image/png'))
    }
    img.src = dataUrl
  })
}

export default function ItemCustomizer() {
  const [showWelcome, setShowWelcome] = useState(true)
  const [tab, setTab] = useState('converter')
  const [fileText, setFileText] = useState('')
  const [outputText, setOutputText] = useState('')
  const [processing, setProcessing] = useState(false)
  const [progress, setProgress] = useState(0)
  const [itemsFound, setItemsFound] = useState(0)
  const [format, setFormat] = useState('optimized')
  const fileInputRef = useRef(null)
  const [builder, setBuilder] = useState({ key: '', label: '', weight: 100, type: 'item', image: '' })
  const [images, setImages] = useState([])
  const [resizeProgress, setResizeProgress] = useState(0)
  const [diffBefore, setDiffBefore] = useState('')
  const [diffAfter, setDiffAfter] = useState('')

  function readFileAsText(file) {
    return new Promise((res, rej) => {
      const r = new FileReader()
      r.onload = () => res(r.result)
      r.onerror = rej
      r.readAsText(file)
    })
  }

  async function handleUpload(e) {
    const f = e.target.files?.[0]
    if (!f) return
    const txt = await readFileAsText(f)
    setFileText(txt)
    setDiffBefore(txt)
    const parsed = parseItemsFromLua(txt)
    setItemsFound(parsed.length)
  }

  async function convert() {
    if (!fileText) return
    setProcessing(true); setProgress(0); setOutputText('')
    const chunks = 8
    for (let i = 0; i < chunks; i++) {
      // eslint-disable-next-line no-await-in-loop
      await new Promise(r => setTimeout(r, 50))
      setProgress(Math.round(((i + 1) / chunks) * 100))
    }
    const { out, count } = parseAndConvert(fileText, format)
    setItemsFound(count)
    setOutputText(out || '-- No items detected. Ensure your file uses a supported format. --')
    setDiffAfter(out)
    setProcessing(false)
  }

  function downloadOutput() {
    if (!outputText) return
    const blob = new Blob([outputText], { type: 'text/plain' })
    const url = URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = format === 'json' ? 'converted_items.json' : format === 'pipe' ? 'converted_items.txt' : 'converted_items.lua'
    a.click()
    URL.revokeObjectURL(url)
  }

  function onBuilderNameChange(val) { setBuilder(suggestFromName(val)) }

  function buildItem() {
    const desc = builder.label ? builder.label : PLACEHOLDER_DESC
    const originalBlock = `['${builder.key}'] = { name = '${builder.key}', label = '${builder.label}', weight = ${Number(builder.weight)||0}, type = '${builder.type}', image = '${builder.image}', unique = false, useable = true, shouldClose = true, description = '${(desc||'').replace(/'/g, "\\'")}' }`
    const { out } = parseAndConvert(originalBlock, format)
    setOutputText(out)
    setDiffAfter(out)
  }

  function onDropImages(e) {
    e.preventDefault()
    const files = Array.from(e.dataTransfer?.files || e.target.files || [])
    const imgs = files.filter(f => f.type.startsWith('image/'))
    setImages(imgs)
  }

  async function resizeAll() {
    setResizeProgress(0)
    if (!images.length) return
    const total = images.length
    for (let i = 0; i < images.length; i++) {
      const file = images[i]
      // eslint-disable-next-line no-await-in-loop
      const dataUrl = await readFileAsDataURL(file)
      // eslint-disable-next-line no-await-in-loop
      const resized = await resizeDataUrl(dataUrl, 100, 100)
      const blob = dataURLToBlob(resized)
      const url = URL.createObjectURL(blob)
      const a = document.createElement('a')
      a.href = url
      a.download = file.name
      a.click()
      URL.revokeObjectURL(url)
      setResizeProgress(Math.round(((i + 1) / total) * 100))
    }
  }

  // ==== Subcomponents ====
  const Header = () => (
    <motion.header 
      initial={{ opacity: 0, y: -8 }} 
      animate={{ opacity: 1, y: 0 }} 
      transition={{ duration: .3 }} 
      className='mb-6'
    >
      <div className='flex items-center justify-between'>
        <div className='flex items-center gap-3'>
          <div className='h-9 w-9 rounded-xl bg-gradient-to-br from-kuro-green to-kuro-purple shadow-lg shadow-emerald-500/10' />
          <div>
            <h1 className='text-xl md:text-2xl font-semibold tracking-tight text-white'>Kuro Item Customizer</h1>
            <p className='text-xs text-gray-400'>Convert • Build • Resize • Diff — blazing-fast, zero deps</p>
          </div>
        </div>
        <div className='flex gap-2 items-center text-sm text-gray-300'>
          <span className='px-2 py-1 rounded-full bg-white/5 border border-white/10'>{itemsFound} items</span>
          <span className='px-2 py-1 rounded-full bg-white/5 border border-white/10'>Format: {format}</span>
        </div>
      </div>
    </motion.header>
  )

  const Tabs = () => (
    <motion.nav initial={{ opacity: 0 }} animate={{ opacity: 1 }} className='mb-6'>
      <div className='flex flex-wrap gap-2 items-center'>
        {[
          { key: 'converter', label: 'Converter', icon: <FileCode size={16} /> },
          { key: 'builder', label: 'Item Builder', icon: <Hammer size={16} /> },
          { key: 'images', label: 'Image Resizer', icon: <Image size={16} /> },
          { key: 'diff', label: 'Diff Viewer', icon: <GitCompare size={16} /> },
        ].map(t => (
          <button
            key={t.key}
            onClick={() => setTab(t.key)}
            className={`group px-4 py-2 rounded-xl border text-sm flex items-center gap-2 transition ${
              tab === t.key 
                ? 'bg-gradient-to-r from-kuro-green/20 to-kuro-purple/20 border-kuro-green/40 text-white' 
                : 'bg-white/5 border-white/10 hover:border-white/20 text-gray-300'
            }`}
          >
            <span className={`${tab === t.key ? 'text-kuro-green' : 'text-gray-300 group-hover:text-white'}`}>
              {t.icon}
            </span>
            {t.label}
          </button>
        ))}
        <div className='ml-auto flex gap-2 items-center'>
          <select 
            value={format} 
            onChange={e => setFormat(e.target.value)} 
            className='bg-gray-800 border border-white/10 px-3 py-2 rounded-xl text-sm text-white focus:outline-none focus:ring-2 focus:ring-kuro-green/40'
            style={{
              backgroundImage: `url("data:image/svg+xml;charset=UTF-8,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='white' stroke-width='2' stroke-linecap='round' stroke-linejoin='round'%3e%3cpolyline points='6,9 12,15 18,9'%3e%3c/polyline%3e%3c/svg%3e")`,
              backgroundRepeat: 'no-repeat',
              backgroundPosition: 'right 8px center',
              backgroundSize: '16px',
              paddingRight: '32px',
              appearance: 'none'
            }}
          >
            <option value='optimized' style={{ backgroundColor: '#1f2937', color: 'white' }}>Optimized (recommended)</option>
            <option value='original' style={{ backgroundColor: '#1f2937', color: 'white' }}>Original Block</option>
            <option value='pipe' style={{ backgroundColor: '#1f2937', color: 'white' }}>Compact Pipe</option>
            <option value='json' style={{ backgroundColor: '#1f2937', color: 'white' }}>JSON</option>
            <option value='ox' style={{ backgroundColor: '#1f2937', color: 'white' }}>OX Inventory</option>
          </select>
          <button 
            onClick={() => fileInputRef.current?.click()} 
            className='px-3 py-2 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple text-sm font-medium shadow-lg shadow-emerald-500/20 flex items-center gap-2 text-white'
          >
            <Upload size={16} /> Upload items.lua
          </button>
          <input ref={fileInputRef} type='file' accept='.lua,.txt' onChange={handleUpload} className='hidden' />
        </div>
      </div>
    </motion.nav>
  )

  const Converter = () => (
    <section>
      <div className='grid md:grid-cols-2 gap-4'>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <div className='flex items-center justify-between'>
            <label className='text-sm text-gray-300'>Input (items.lua)</label>
            <kbd className='text-[10px] px-2 py-1 rounded bg-black/40 border border-white/10 text-gray-300'>Ctrl + V</kbd>
          </div>
          <textarea 
            value={fileText} 
            onChange={e => setFileText(e.target.value)} 
            rows={14} 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm font-mono border border-white/10 focus:outline-none focus:ring-2 focus:ring-kuro-green/40 text-white'
          />
        </motion.div>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <div className='flex items-center justify-between'>
            <label className='text-sm text-gray-300'>Output</label>
            <div className='flex gap-2'>
              <button 
                onClick={() => navigator.clipboard.writeText(outputText)} 
                disabled={!outputText} 
                className='text-xs px-2 py-1 rounded border border-white/10 bg-white/5 hover:border-white/20 flex items-center gap-1 text-gray-300'
              >
                <ClipboardCopy size={14} /> Copy
              </button>
              <button 
                onClick={downloadOutput} 
                disabled={!outputText} 
                className='text-xs px-2 py-1 rounded border border-white/10 bg-white/5 hover:border-white/20 flex items-center gap-1 text-gray-300'
              >
                <Download size={14} /> Download
              </button>
            </div>
          </div>
          <textarea 
            value={outputText} 
            readOnly 
            rows={14} 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm font-mono border border-white/10 text-green-400'
          />
        </motion.div>
      </div>
      <div className='flex items-center gap-3 mt-4'>
        <button 
          onClick={convert} 
          disabled={processing || !fileText} 
          className='px-4 py-2 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple font-medium shadow-lg shadow-emerald-500/20 flex items-center gap-2 text-white'
        >
          <Wand2 size={16} /> Convert
        </button>
        <div className='flex-1 h-2 rounded-full bg-white/10 overflow-hidden'>
          <motion.div 
            initial={{ width: 0 }} 
            animate={{ width: `${progress}%` }} 
            className='h-full bg-kuro-green'
          />
        </div>
        <div className='text-sm text-gray-300 w-16 text-right'>{progress}%</div>
      </div>
      <div className='text-xs text-gray-400 mt-3 flex items-start gap-2'>
        ⚠️ <span>Parser reads unique/useable/shouldClose from source. Missing description falls back to label, then placeholder.</span>
      </div>
    </section>
  )

  const Builder = () => (
    <section>
      <div className='grid md:grid-cols-2 gap-4'>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <label className='text-sm text-gray-300'>Item Key</label>
          <input 
            value={builder.key} 
            onChange={e => onBuilderNameChange(e.target.value)} 
            placeholder='custom_weapon' 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:outline-none focus:ring-2 focus:ring-kuro-green/40 text-white'
          />
          <label className='text-sm text-gray-300 mt-3 block'>Label</label>
          <input 
            value={builder.label} 
            onChange={e => setBuilder(v => ({ ...v, label: e.target.value }))} 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:outline-none focus:ring-2 focus:ring-kuro-green/40 text-white'
          />
          <div className='grid grid-cols-3 gap-2 mt-3'>
            <input 
              type='number' 
              value={builder.weight} 
              onChange={e => setBuilder(v => ({ ...v, weight: Number(e.target.value) }))} 
              className='p-2 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:ring-2 focus:ring-kuro-green/40 text-white'
            />
            <input 
              value={builder.type} 
              onChange={e => setBuilder(v => ({ ...v, type: e.target.value }))} 
              className='p-2 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:ring-2 focus:ring-kuro-green/40 text-white'
            />
            <input 
              value={builder.image} 
              onChange={e => setBuilder(v => ({ ...v, image: e.target.value }))} 
              className='p-2 bg-[#0b0f1a] rounded-lg text-sm border border-white/10 focus:ring-2 focus:ring-kuro-green/40 text-white'
            />
          </div>
          <div className='flex gap-2 mt-4'>
            <button 
              onClick={buildItem} 
              className='px-4 py-2 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple font-medium shadow-lg shadow-emerald-500/20 text-white'
            >
              Build Item
            </button>
            <button 
              onClick={() => { 
                setBuilder({ key: '', label: '', weight: 100, type: 'item', image: '' }); 
                setOutputText(''); 
              }} 
              className='px-4 py-2 rounded-xl bg-white/5 border border-white/10 text-gray-300'
            >
              Clear
            </button>
          </div>
        </motion.div>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <label className='text-sm text-gray-300'>Preview</label>
          <pre className='mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm overflow-auto font-mono border border-white/10 text-green-400'>
            {outputText || '-- built item will show here --'}
          </pre>
        </motion.div>
      </div>
    </section>
  )

  const ImagesTab = () => (
    <section>
      <motion.div 
        initial={{ opacity: 0, y: 8 }} 
        animate={{ opacity: 1, y: 0 }} 
        onDragOver={e => e.preventDefault()} 
        onDrop={onDropImages} 
        className='border-2 border-dashed border-white/15 p-6 rounded-xl text-center bg-black/20'
      >
        <p className='text-sm text-gray-300'>
          Drag & drop images here or{' '}
          <button 
            onClick={() => document.getElementById('imgIn')?.click()} 
            className='underline text-kuro-green'
          >
            browse
          </button>
        </p>
        <input 
          id='imgIn' 
          type='file' 
          multiple 
          accept='image/*' 
          onChange={onDropImages} 
          className='hidden'
        />
        <div className='mt-4 text-xs text-gray-400'>{images.length} image(s) ready</div>
        <div className='flex gap-2 justify-center mt-4'>
          <button 
            onClick={resizeAll} 
            className='px-4 py-2 rounded-xl bg-gradient-to-r from-kuro-green to-kuro-purple font-medium shadow-lg shadow-emerald-500/20 text-white'
          >
            Resize to 100x100
          </button>
          <button 
            onClick={() => setImages([])} 
            className='px-4 py-2 rounded-xl bg-white/5 border border-white/10 text-gray-300'
          >
            Clear
          </button>
        </div>
        <div className='mt-4'>
          <div className='bg-white/10 h-2 rounded-full overflow-hidden'>
            <motion.div 
              initial={{ width: 0 }} 
              animate={{ width: `${resizeProgress}%` }} 
              className='h-full bg-kuro-green'
            />
          </div>
        </div>
      </motion.div>
      <div className='mt-4 grid grid-cols-2 md:grid-cols-6 gap-2'>
        {images.map((f, i) => (
          <motion.div 
            key={i} 
            initial={{ opacity: 0, y: 6 }} 
            animate={{ opacity: 1, y: 0 }} 
            className='bg-[#0b0f1a] p-2 rounded-lg text-xs text-gray-300 border border-white/10 truncate'
          >
            {f.name}
          </motion.div>
        ))}
      </div>
    </section>
  )

  const DiffTab = () => (
    <section>
      <div className='grid md:grid-cols-2 gap-4'>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <label className='text-sm text-gray-300'>Before</label>
          <textarea 
            value={diffBefore} 
            onChange={e => setDiffBefore(e.target.value)} 
            rows={14} 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm font-mono border border-white/10 text-white'
          />
        </motion.div>
        <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }}>
          <div className='flex items-center justify-between'>
            <label className='text-sm text-gray-300'>After</label>
            <div className='flex gap-2'>
              <button 
                onClick={() => setDiffAfter(outputText)} 
                className='text-xs px-2 py-1 rounded border border-white/10 bg-white/5 hover:border-white/20 flex items-center gap-1 text-gray-300'
              >
                <RefreshCw size={14} /> From Output
              </button>
              <button 
                onClick={() => navigator.clipboard.writeText(diffAfter)} 
                className='text-xs px-2 py-1 rounded border border-white/10 bg-white/5 hover:border-white/20 flex items-center gap-1 text-gray-300'
              >
                <ClipboardCopy size={14} /> Copy
              </button>
            </div>
          </div>
          <textarea 
            value={diffAfter} 
            onChange={e => setDiffAfter(e.target.value)} 
            rows={14} 
            className='w-full mt-2 p-3 bg-[#0b0f1a] rounded-lg text-sm font-mono border border-white/10 text-green-400'
          />
        </motion.div>
      </div>
      <div className='mt-4 text-xs text-gray-400'>
        Review changes before replacing your server file. Always keep a backup.
      </div>
    </section>
  )

  // Welcome Screen Component
  const WelcomeScreen = () => (
    <div className='min-h-screen relative bg-[radial-gradient(ellipse_at_top,_var(--tw-gradient-stops))] from-[#0b1220] via-[#0b0f1a] to-black text-gray-100 flex items-center justify-center'>
      <div className='pointer-events-none absolute inset-0 opacity-20 [mask-image:radial-gradient(transparent,black)]'>
        <div className='absolute inset-0 bg-[linear-gradient(90deg,rgba(255,255,255,.06)_1px,transparent_1px),linear-gradient(rgba(255,255,255,.06)_1px,transparent_1px)] bg-[size:32px_32px]' />
      </div>
      <motion.div 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className='relative text-center max-w-2xl px-8'
      >
        <motion.div 
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.6 }}
          className='w-32 h-32 mx-auto mb-8 rounded-3xl bg-gradient-to-br from-kuro-green to-kuro-purple shadow-2xl shadow-emerald-500/20 flex items-center justify-center'
        >
          <span className='text-5xl'>🛠️</span>
        </motion.div>
        
        <motion.h1 
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4, duration: 0.6 }}
          className='text-5xl md:text-6xl font-bold mb-6 bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent'
        >
          Kuro Item Customizer
        </motion.h1>
        
        <motion.p 
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.6, duration: 0.6 }}
          className='text-xl text-gray-300 mb-12 leading-relaxed'
        >
          The ultimate tool for FiveM item management.<br />
          Convert • Build • Resize • Compare — blazing-fast, zero dependencies.
        </motion.p>
        
        <motion.div 
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.8, duration: 0.6 }}
          className='space-y-4'
        >
          <button
            onClick={() => setShowWelcome(false)}
            className='px-8 py-4 bg-gradient-to-r from-kuro-green to-kuro-purple text-white font-semibold rounded-2xl shadow-2xl shadow-emerald-500/25 hover:shadow-emerald-500/40 transition-all duration-300 transform hover:scale-105'
          >
            Get Started
          </button>
          
          <div className='flex items-center justify-center gap-8 text-sm text-gray-400 mt-8'>
            <div className='flex items-center gap-2'>
              <span className='w-2 h-2 bg-kuro-green rounded-full'></span>
              <span>QB ↔ OX Conversion</span>
            </div>
            <div className='flex items-center gap-2'>
              <span className='w-2 h-2 bg-kuro-purple rounded-full'></span>
              <span>5 Export Formats</span>
            </div>
            <div className='flex items-center gap-2'>
              <span className='w-2 h-2 bg-blue-400 rounded-full'></span>
              <span>Image Resizing</span>
            </div>
          </div>
        </motion.div>
      </motion.div>
    </div>
  )

  if (showWelcome) {
    return <WelcomeScreen />
  }

  return (
    <div className='min-h-screen relative bg-[radial-gradient(ellipse_at_top,_var(--tw-gradient-stops))] from-[#0b1220] via-[#0b0f1a] to-black text-gray-100'>
      {/* subtle grid overlay */}
      <div className='pointer-events-none absolute inset-0 opacity-20 [mask-image:radial-gradient(transparent,black)]'>
        <div className='absolute inset-0 bg-[linear-gradient(90deg,rgba(255,255,255,.06)_1px,transparent_1px),linear-gradient(rgba(255,255,255,.06)_1px,transparent_1px)] bg-[size:32px_32px]' />
      </div>
      <div className='relative p-6 max-w-6xl mx-auto'>
        <Header />
        <Tabs />
        <main className='rounded-2xl border border-white/10 bg-white/5 backdrop-blur-xl p-4 md:p-5 shadow-xl shadow-black/30'>
          {tab === 'converter' && <Converter />}
          {tab === 'builder' && <Builder />}
          {tab === 'images' && <ImagesTab />}
          {tab === 'diff' && <DiffTab />}
        </main>
        <footer className='mt-6 text-xs text-gray-400 flex flex-wrap items-center gap-2'>
          <span>Made for QBCore item conversion.</span>
          <span className='opacity-50'>•</span>
          <span>No flashing animations (epilepsy‑safe).</span>
          <span className='opacity-50'>•</span>
          <span>Fav accent: <span className='text-kuro-green'>#55fa9a</span></span>
        </footer>
      </div>
    </div>
  )
}
